import { useContext } from 'react';
import { AppContext } from '../context/AppContext';
import { Bar } from 'react-chartjs-2';
import FoodCard from './FoodCard';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const { stats, recentListings, setShowModal, formatNumber } = useContext(AppContext);

  const chartData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
    datasets: [{
      label: 'Food Donations (kg)',
      data: [120, 190, 150, 220, 180, 300, 280, 250, 320, 350, 400, 380],
      backgroundColor: '#4CAF50',
      borderColor: '#388E3C',
      borderWidth: 1
    }]
  };

  const chartOptions = {
    responsive: true,
    scales: {
      y: {
        beginAtZero: true
      }
    }
  };

  return (
    <section className="dashboard" id="dashboard">
      <div className="dashboard-header">
        <h2 className="section-title">Organization Dashboard</h2>
        <button className="btn btn-secondary" onClick={() => setShowModal('donate')}>Add Food Listing</button>
      </div>
      
      <div className="stats-grid">
        <div className="stat-card">
          <h3>{stats.totalDonations}</h3>
          <p>Total Donations</p>
        </div>
        <div className="stat-card">
          <h3>{formatNumber(stats.mealsProvided)}</h3>
          <p>Meals Provided</p>
        </div>
        <div className="stat-card">
          <h3>{stats.co2Reduced}</h3>
          <p>COâ‚‚ Reduced (tons)</p>
        </div>
        <div className="stat-card">
          <h3>{stats.activeDrivers}</h3>
          <p>Active Drivers</p>
        </div>
      </div>
      
      <div className="chart-container">
        <h3>Monthly Food Distribution</h3>
        <Bar data={chartData} options={chartOptions} height={300} />
      </div>
      
      <div className="recent-listings">
        <h3>Recent Food Listings</h3>
        <div className="food-cards">
          {recentListings.map(listing => (
            <FoodCard key={listing.id} listing={listing} />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Dashboard;
